INSERT INTO PRODUCTS (id, title, description, price) 
values
(uuid_generate_v1(), 'Поднятие уровня в одиночку', '10 лет назад, после того, как открылись «Врата», соединившие реальный мир с параллельным, некоторые из людей получили силу охотиться на монстров внутри «Врат». Они известны как «Охотники». Однако не все Охотники сильные. Меня зовут Сун Джин-Ву, охотник E-ранга. Я тот, кто рискует своей жизнью в самых низких уровнях подземелья. Не имея никаких сверхсильных навыков, я едва зарабатывал необходимые деньги, сражаясь в низкоуровневых подземельях... по крайней мере, пока я не нашел скрытое подземелье с самыми трудными проблемами в подземельях D-ранга! В конце концов, когда я чуть не умер, я внезапно получил странную силу, журнал квестов, который мог видеть только я, секрет для поднятия уровня, о котором знаю только я! Если я тренировался в соответствии с моими квестами и охотился на монстров, то мой уровень повышался. Переход от самого слабого Охотника к самому сильному, Охотнику S-ранга!', 10),
(uuid_generate_v1(), 'Крутой учитель Онидзука', 'Это вдохновляющая история о панке и преступнике, ставшем учителем. 22-летний Онидзука вел тяжелую жизнь, пока не решил исправиться и не стал школьным учителем. Это очень смешная манга, не лишенная морали.', 10),
(uuid_generate_v1(), 'Созданный в бездне', 'трогательная история про сироту по имени Рико, которая мечтает стать таким же великим искателем, как ее мать, и разгадать великую тайну «Бездны». Однажды, исследуя темные глубины, она натыкается на мальчика, который оказывается роботом…', 8),
(uuid_generate_v1(), 'Необъятный океан', 'Любишь спортивное аниме и мангу? Тогда «Океан» тебе тоже понравится. Это своего рода серия спортивной манги, но все тут завязано на плавании. Только это не какой-то там «Свободный стиль», это совершенно другая вещь. И создатели отлично поработали, чтобы плавание выглядело действительно хорошо в черно-белом цвете.', 7),
(uuid_generate_v1(), 'Бродяга', 'Манга, созданная Такэхико Иноуэ и основаная на романе Эйдзи Ёсикавы о жизни фехтовальщика Миямото Мусаси.  В этом историческом сейнене есть все, о чем можно мечтать — интриги, драма, боевик, множество красиво нарисованных боевых сцен и даже тяжелые эмоциональные сцены.', 8),
(uuid_generate_v1(), 'Ван Пис', 'Эта манга даже не нуждается в представлении. «Ван Пис» — любимое аниме миллионов людей по всему миру. В основе сюжета мальчик, который хочет стать королем всех пиратов. Поэтому он бороздит океан, набирает себе самую лучшую команду и, конечно же, сражается со злом.', 15),
(uuid_generate_v1(), 'Монстр', 'Этот сэйнэн — не традиционная манга; она нетипична и этим интересна. История вращается вокруг моральных и этических дилемм доктора Кензо Тенма, который решает спасти жизнь ребенка, а не жизнь мэра. Однако людей вокруг него начинают убивать, и все улци ведут именно на спасенного им маленького мальчика.', 10),
(uuid_generate_v1(), 'Стальной алхимик', 'Еще одна очень знаменитая манга, по которой было снято одноименное аниме. Если кратко, то главные герои — два брата — решили с помощью алхимии воскресить умершую мать. И что же могло пойти не так у детей, которые толком не умеют владеть этими силами? У старшего половина тела стали механическими, а младший и вовсе оказался запертым в доспехах с ног до головы. И теперь братьям предстоит долгий путь, чтобы исправить ситуацию и вернуть себе тела.', 10),
(uuid_generate_v1(), 'Невероятные приключения ДжоДжо', 'Даже если ты далека от аниме и манги, то все равно видела мемчики по «ДжоДжо» в ТикТоке. Кто он такой и почему популярен — можешь почитать тут.', 18),
(uuid_generate_v1(), 'Берсерк', 'Поклонникам боевиков и / или фэнтези это понравится, но чтение первых нескольких томов может быть довольно затруднительным, поскольку рисовка немного устарела, а сюжет развивается очень медленно. Но подождать определенно стоит. Историческая манга с нотками фэнтези и эпоса. В центре сюжета — истории жестокого и властного наемника Гатса и легендарного вожака отряда Соколов Гриффитса.', 20);
